package Com.tnsif.Practice2;

public class StarPattern
{

	@SuppressWarnings("unused")
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
int i,j,row=5;
for(j=0;j<=row;j++)
{
	for(i=0;i<=j;i++)
	{
		System.out.println("*");
	}
	System.out.println("\t");
}
	}

}
